	    if (v.game.discard != undefined && v.game.discard.length > 0) {
	        var d1 = $('<div><h2>Played cards</h2></div>');
	        _.each(v.game.discard, function(carte) {
                var d = $('<div class="discardHolder" .>');

                d.append(giveCard(carte));
                d1.append(d);
            });
            discardBody.append(d1);
	    }

		var cartes = [];
